function getFechaActual(){
  var today = new Date();
  var dd = today.getDate();
  var mm = today.getMonth()+1; //January is 0!
  var yyyy = today.getFullYear();
  if(dd<10)
    dd='0'+dd;

  if(mm<10) 
    mm='0'+mm;
 
  return yyyy+'-'+mm+'-'+dd;
}

function getHoraActual(){
  var now = new Date();
  var hh = now.getHours();
  var mm = now.getMinutes();  
  return hh+':'+mm;
}

